#define LCD_1602_EN (1 << 2)
#define LCD_1602_RS (1 << 0)

void LCD_1602_I2C_Send_Nibble(char nibble) {
    I2C_Start(0x27);
    I2C_Send(nibble | LCD_1602_EN);
    I2C_End();

    delay(5);

    I2C_Start(0x27);
    I2C_Send(nibble & ~LCD_1602_EN);
    I2C_End();
    delay(5);
}

void LCD_1602_I2C_Send_Command(char byte) {
    LCD_1602_I2C_Send_Nibble(byte & 0xF0);
    LCD_1602_I2C_Send_Nibble((byte << 4) & 0xF0 );
}

void LCD_1602_I2C_Send_Data(char byte) {
    LCD_1602_I2C_Send_Nibble(LCD_1602_RS | (byte & 0xF0));
    LCD_1602_I2C_Send_Nibble(LCD_1602_RS | ((byte << 4) &  0xF0));
}

void main() {
    enablePLLAsSystemClock(PLL_Speed_128Mhz);

#if defined(SYSTICK_CLKSOURCE_EXTERNAL)
    SysTickReloadValue = 8000; // Clock source is external 8MHz
#elif defined(SYSTICK_CLKSOURCE_INTERNAL)
    SysTickControlAndStatus->CLKSOURCE = 1;
    SysTickReloadValue = 64000; // Clock source is AHB which is 64MHz
#endif
    SysTickControlAndStatus->TICKINT = 1;
    SysTickControlAndStatus->ENABLE = 1;

    enableClocks(BLUEPILL_ALL_APB2_INTERFACES_CLOCK);
    enableClocks(BLUEPILL_ALL_APB1_INTERFACES_CLOCK);

    pinMode(PB6, ALTERNATE_OPEN_DRAIN);
    pinMode(PB7, ALTERNATE_OPEN_DRAIN);

    pinMode(PC13, OUTPUT_ULTRA_FAST);

    delay(1000);

    I2C_Init();
    OLED_I2C_Init();

    OLED_I2C_Write(0, 0, "Hi");
    OLED_I2C_Write(0, 1, "There :)");
    OLED_I2C_Write(0, 2, "I am");
    OLED_I2C_Write(0, 3, "Coo00000***L");


    LCD_1602_I2C_Send_Command(0x03);  // Initialize in 8-bit mode
    LCD_1602_I2C_Send_Command(0x03);  // Initialize in 8-bit mode
    LCD_1602_I2C_Send_Command(0x03);  // Initialize in 8-bit mode
    LCD_1602_I2C_Send_Command(0x02);  // Initialize in 4-bit mode
    LCD_1602_I2C_Send_Command(0x28);
    LCD_1602_I2C_Send_Command(0x08);  // Display off
    LCD_1602_I2C_Send_Command(0x01);  // Clear display
    LCD_1602_I2C_Send_Command(0x06);  // Entry mode set: Increment cursor, No display shift
    LCD_1602_I2C_Send_Command(0x0C);  // Display on, Cursor off, Blink off


    LCD_1602_I2C_Send_Data('A');
    LCD_1602_I2C_Send_Data('A');
    LCD_1602_I2C_Send_Data('C');
    LCD_1602_I2C_Send_Data('*');
    while(1) {
//        I2C_Read(0x0D); // 0x1E for the non chinese-knockoff
//        I2C_Send(0x03);
//        while (!I2C_SR1->RxNE);
//        x = I2C_DR;
//        I2C_End();

        I2C_Start(0x27);
        I2C_Send(0b0101 << 4 | 1);
        I2C_Send(0b0011 << 4 | 1);
        I2C_End();

        digitalWrite(PC13, HIGH);
        delay(500);
        digitalWrite(PC13, LOW);
        delay(500);
    }
}