void main() {
    enablePLLAsSystemClock(PLL_Speed_128Mhz);
    enableClocks(BLUEPILL_ALL_APB2_INTERFACES_CLOCK);
    enableClocks(BLUEPILL_ALL_APB1_INTERFACES_CLOCK);

    pinMode(PB6, ALTERNATE_OPEN_DRAIN);
    pinMode(PB7, ALTERNATE_OPEN_DRAIN);

    pinMode(PC13, OUTPUT_ULTRA_FAST);

    I2C_Init();

    I2C_Start(0x3C);
    I2C_Send(0x00);
    I2C_Send(0xAE);
    I2C_Send(0xD5);
    I2C_Send(0xF0);
    I2C_Send(0xA8);
    I2C_Send(64 - 1);
    I2C_Send(0x8D);
    I2C_Send(0x14);
    I2C_Send(0xAF);
    I2C_Send(0xA4);
    I2C_End();

    I2C_Start(0x3C);
    I2C_Send(0x00);
    I2C_Send(0x20);
    I2C_Send(0x00);
    I2C_End();

    while(1) {
        I2C_Start(0x3C);
        I2C_Send(0x00);
        I2C_Send(0x21);
        I2C_Send(0);
        I2C_Send(127);
        I2C_Send(0x22);
        I2C_Send(0);
        I2C_Send(7);
        I2C_End();

        for (int i = 0; i < 1024; i++) {
            I2C_Start(0x3C);
            I2C_Send(0x40);
            I2C_Send(data[i]);
            I2C_End();
        }

        delay(200000);
        digitalWrite(PC13, HIGH);
        delay(200000);
        digitalWrite(PC13, LOW);
    }
}